import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-reactive-forms',
  templateUrl: './reactive-forms.component.html',
  styleUrls: ['./reactive-forms.component.scss']
})
export class ReactiveFormsComponent implements OnInit{

  public cadastroForm: FormGroup = this.formBuilder.group({
    firstName: ['', Validators.required],
    lastName: ['', [Validators.required, Validators.minLength(4)]],
  })
  constructor(private formBuilder: FormBuilder) {}

  ngOnInit(): void {
    throw new Error('Method not implemented.');
  }

  public subimitForm() {
    if(this.cadastroForm.valid) {
      console.log(this.cadastroForm.value);
      console.log(this.cadastroForm.value.firstName);
      console.log(this.cadastroForm.value.lastName);
    }
  }
}
<form [formGroup]="cadastroForm" (ngSubmit)="subimitForm()">
  <label>
    Nome: 
    <input type="text" formControlName="firstName">
    <span *ngIf="cadastroForm.get('firstName')?.errors && cadastroForm.get('firstName')?.touched && cadastroForm.get('firstName')?.dirty">Favor adicionar um nome</span>
  </label>

  <br>
  <br>
  <label>
    Sobrenome: 
    <input type="text" formControlName="lastName" maxlength="5">
  </label>

  <button [disabled]="cadastroForm.invalid">Enviar</button>
  <!--{{cadastroForm.get('firstName')?.value}}-->
   <!--{{cadastroForm.get('lastName')?.value}}-->
   <!--
    {{cadastroForm.get('firstName')?.errors | json}}
    {{cadastroForm.get('firstName')?.touched | json}}
    {{cadastroForm.get('firstName')?.dirty | json}}
   -->

   <p>
   {{cadastroForm.get('lastName')?.errors | json }}
   </p>
</form>
